  /**
   * filePath: src/store/modules/sensitive-words/index.ts
   * description: 定义数据接口，获取数据，并对数据进行增删改查
   */
  import { defineStore } from 'pinia';
  import { ISensitiveWords } from './types';
  import { ISensitiveWord, getList, addWords, updateWords, deleteWords } from '@/api/sensitive-words';

  const useSensitiveWordsStore = defineStore('useSensitiveWordsStore', {
    state: (): ISensitiveWords => {
      return {
        wordList: [],
      };
    },
    actions: {
      asyncList(params?: any) {
        return getList(params).then(({ code, data }) => {
          const result = [...data].reverse().map((item, key) => ({ ...item, key: item.word, index: key + 1 }));
          this.wordList = result;
          return result;
        });
      },
      addWords(data: ISensitiveWord[]) {
        return addWords(data).then((data) => {
          this.asyncList();
          return data;
        });
      },
      updateWords(data: ISensitiveWord) {
        return updateWords(data).then((data) => {
          this.asyncList();
          return data;
        });
      },
      deleteWords(data: string[]) {
        return deleteWords(data).then((data) => {
          this.asyncList();
          return data;
        });
      }
    },
  });

  export default useSensitiveWordsStore;

