        <a-input v-model="form.name" :placeholder="$t('user.editAccount.fillAccountName')" />
      </a-form-item>
      <a-form-item field="roleKeys" :label="$t('user.table.role')" required :rules="[{ required: true, message: $t('user.addAccount.roleNotNull') }]">
        <a-select v-model="form.roleKeys" multiple allow-search :placeholder="$t('user.addAccount.selectRole')" allow-clear>
          <a-option v-for="role in roleList" :key="role.roleKey" :value="role.roleKey">{{ role.name }}</a-option>
        </a-select>
      </a-form-item>
      <a-form-item field="appKeys" :label="$t('user.addAccount.application')" required :rules="[{ required: true, message: $t('user.editAccount.applicationNotNull') }]">
        <a-select v-model="form.appKeys" multiple allow-search :placeholder="$t('user.editAccount.selectApplication')" @search="handleSearch" allow-clear>
          <a-option v-for="app in appOptionList" :key="`${app.prodAppKey}-${app.testAppKey}`" :value="`${app.prodAppKey ?? ''}${middleFix}${app.testAppKey ?? ''}`">{{ app.name }}</a-option>
        </a-select>
      </a-form-item>
    </a-form>
    <template #footer>
      <a-button type="secondary" @click="handleCancel">{{ $t('user.addAccount.cancel') }}</a-button>
      <a-button type="primary" :loading="loading" @click="handleSubmit">{{ $t('user.addAccount.submit') }}</a-button>
    </template>
  </a-modal>
</template>
<script lang="ts" setup>
  import { ISubAccount, IApp, IAddSubAccountParams, updateSlaveUser, IRole, getRoleList, getAssignableAppList } from '@/api/user';
  import { Message } from '@arco-design/web-vue';
  import { useI18n } from 'vue-i18n';
  import { reactive, computed, ref, onMounted, watch } from 'vue';

  const props = defineProps<{
    visible: any;
    accountInfo: ISubAccount;
  }>();

  const emits = defineEmits<{
    (id: 'setVisible', value: boolean): void;
    (id: 'reloadList'): void;
  }>();

  const middleFix = ':ID:ID:';
  const visible = computed(() => props.visible);
  const formRef = ref();
  const loading = ref(false);
  const form = reactive<IAddSubAccountParams>({
    email: '',
    name: '',
    roleKeys: [],
    appKeys: [],
  });

  const init = ref<number>(0);
  watch(props.accountInfo, (accountInfo) => {
    init.value = 0;
    form.email = accountInfo.email;
    form.name = accountInfo.name;
    form.roleKeys = accountInfo?.roles?.map((role: IRole) => role.roleKey) || [];
    form.appKeys = accountInfo?.apps?.map((app: IApp) => `${app.prodAppKey ?? ''}${middleFix}${app.testAppKey ?? ''}`) || [];
  });

  const roleList = ref<IRole[]>([]);
  const appList = ref<IApp[]>([]);
  const searchValue = ref<string>('');
  const appOptionList = computed(() => {
    const value = searchValue.value;
    return appList.value.filter((item) => new RegExp(value).test(item.name) || new RegExp(value).test(item.prodAppKey) || new RegExp(value).test(item.testAppKey));
  });

  const roleKeys = computed(() => form.roleKeys);
  watch(roleKeys, (currentValue, oldValue) => {
    if (init.value > 0) {
      form.appKeys = [];
    }

    if (currentValue && currentValue.length > 0) {
      getAssignableAppList(currentValue).then(({ data }) => {
        appList.value = data;
        init.value += 1;
      });
      return;
    }
    appList.value = [];
  });

  onMounted(() => {
    getRoleList().then(({ data }) => {
      roleList.value = data;
    });
  });

  const handleSearch = (value: string) => {
    searchValue.value = value;
  };

  const { t } = useI18n();
  const handleSubmit = () => {
    loading.value = true;
    formRef.value.validate().then((res: any) => {
      if (!res) {
        updateSlaveUser({
          ...form,
          userKey: props.accountInfo.userKey,
          appKeys: form.appKeys.reduce((a, b) => a.concat(b.split(middleFix).filter((m) => !!m) as []), []),
        })
          .then(({ code }) => {
            if (code === 10000) {
              emits('setVisible', false);

              Message.success(t('user.editAccount.accountUpdateSuccess', { email: form.name }));
              emits('reloadList');
              Object.assign(form, {
                email: '',
                name: '',
                roleKeys: [],
                appKeys: [],
              });
            }
          })
          .finally(() => {
            loading.value = false;
          });
      } else {
        emits('setVisible', true);
        loading.value = false;
      }
    });
  };
  const handleCancel = () => {
    emits('setVisible', false);
  };
</script>

<script lang="ts">
  export default {
    name: 'AddAccount',
  };
</script>
<style lang="less" scoped></style>
